$(document).ready(function(){google.charts.load('current',{'packages':['bar']});google.charts.setOnLoadCallback(prepareBarChart);function prepareBarChart(){$('.dailyInterestBarChart').each(function(){var errorHappened=false;var $el=$(this);var barChartSource=$el.data('chart');if(barChartSource&&barChartSource.charAt(0)==="#"){var $barChartSourceEl=$(barChartSource);barChartSource=$barChartSourceEl.val();if(!barChartSource){barChartSource=$barChartSourceEl.text();}}
try{var barChartArrayData=JSON.parse(barChartSource);}catch(err){errorHappened=true;}
if(!errorHappened&&barChartArrayData){createBarChart($el[0],barChartArrayData);}});}
function createBarChart($el,barChartdata){var data=new google.visualization.arrayToDataTable(barChartdata);var formatter=new google.visualization.NumberFormat({suffix:'%',fractionDigits:2});formatter.format(data,1);formatter.format(data,2);var options={width:750,colors:['#3184c6','#a2dce7'],chart:{title:'Daily Interest (%)',fontSize:45,subtitle:''},bars:'vertical',vAxis:{format:'decimal'},axes:{x:{0:{side:'top'}}},};var chart=new google.charts.Bar($el);chart.draw(data,options);}});