$(document).ready(function(){$('.form-control').each(function(){if($(this).val()){$(this).addClass('filled')}});});$(document).ready(function(){let url=new URL(window.location.href);let sponsor=url.searchParams.get("sponsor");let campaign=url.searchParams.get("campaign");if(sponsor){localStorage.setItem('sponsor',sponsor);}
if(campaign){$.cookie("CryptoLuxCampaign",campaign,{expires:90});}});$(document).ready(function(){let plainUrl=window.location.href;let url=new URL(plainUrl);let sponsor=url.searchParams.get("sponsor");if(plainUrl.indexOf("register")!==-1&&!sponsor&&localStorage.getItem('sponsor')){url.searchParams.set('sponsor',localStorage.getItem('sponsor'));let campaignValue=$.cookie("CryptoLuxCampaign");if(typeof campaignValue==='string'&&campaignValue.length>0){url.searchParams.set('campaign',campaignValue);}
localStorage.removeItem("sponsor");window.location.href=url.toString();}});